import React, { Component, PropTypes } from 'react';
import classnames from 'classnames';

class Face extends Component {
  static propTypes = {
    active: PropTypes.number,
    numbers: PropTypes.arrayOf(PropTypes.number),
    radius: PropTypes.number,
    spacing: PropTypes.number,
    theme: PropTypes.shape({
      active: PropTypes.string,
      face: PropTypes.string,
      number: PropTypes.string,
    }),
    twoDigits: PropTypes.bool,
  };

  static defaultProps = {
    active: null,
    numbers: [],
    radius: 0,
    twoDigits: false,
  };

  numberStyle(rad, num) {
    return {
      position: 'absolute',
      left: (rad + rad * Math.sin(360 * (Math.PI / 180) / 12 * (num - 1)) + this.props.spacing),
      top: (rad - rad * Math.cos(360 * (Math.PI / 180) / 12 * (num - 1)) + this.props.spacing),
    };
  }

  faceStyle() {
    return {
      height: this.props.radius * 2,
      width: this.props.radius * 2,
    };
  }

  renderNumber = (number, idx) => {
    const { active, radius, spacing, theme, twoDigits } = this.props;
    return (
      <span
        className={classnames(theme.number, { [theme.active]: number === active })}
        style={this.numberStyle(radius - spacing, idx + 1)}
        key={number}
      >
        {twoDigits ? (`0${number}`).slice(-2) : number}
      </span>
    );
  }

  render() {
    const { numbers, onTouchStart, onMouseDown, theme } = this.props; // eslint-disable-line
    return (
      <div
        className={theme.face}
        onTouchStart={onTouchStart}
        onMouseDown={onMouseDown}
        style={this.faceStyle()}
      >
        {numbers.map(this.renderNumber)}
      </div>
    );
  }
}

export default Face;

import React, { Component, PropTypes } from 'react';
import { angle360FromPositions } from '../utils/utils';
import events from '../utils/events';
import prefixer from '../utils/prefixer';

class Hand extends Component {
  static propTypes = {
    angle: PropTypes.number,
    className: PropTypes.string,
    length: PropTypes.number,
    onMove: PropTypes.func,
    onMoved: PropTypes.func,
    origin: PropTypes.shape({
      x: PropTypes.number,
      y: PropTypes.number,
    }),
    step: PropTypes.number,
    theme: PropTypes.shape({
      hand: PropTypes.string,
      knob: PropTypes.string,
    }),
  };

  static defaultProps = {
    className: '',
    angle: 0,
    length: 0,
    origin: {},
  };

  state = {
    knobWidth: 0,
  };

  componentDidMount() {
    setTimeout(() => {
      this.setState({ knobWidth: this.knobNode.offsetWidth });
    });
  }

  componentWillUnmount() {
    events.removeEventsFromDocument(this.getMouseEventMap());
    events.removeEventsFromDocument(this.getTouchEventMap());
  }

  getMouseEventMap() {
    return {
      mousemove: this.handleMouseMove,
      mouseup: this.handleMouseUp,
    };
  }

  getTouchEventMap() {
    return {
      touchmove: this.handleTouchMove,
      touchend: this.handleTouchEnd,
    };
  }

  getPositionRadius(position) {
    const x = this.props.origin.x - position.x;
    const y = this.props.origin.y - position.y;
    return Math.sqrt((x * x) + (y * y));
  }

  mouseStart(event) {
    events.addEventsToDocument(this.getMouseEventMap());
    this.move(events.getMousePosition(event));
  }

  touchStart(event) {
    events.addEventsToDocument(this.getTouchEventMap());
    this.move(events.getTouchPosition(event));
    events.pauseEvent(event);
  }

  handleMouseMove = (event) => {
    this.move(events.getMousePosition(event));
  };

  handleTouchMove = (event) => {
    this.move(events.getTouchPosition(event));
  };

  handleMouseUp = () => {
    this.end(this.getMouseEventMap());
  };

  handleTouchEnd = () => {
    this.end(this.getTouchEventMap());
  };

  trimAngleToValue(angle) {
    return this.props.step * Math.round(angle / this.props.step);
  }

  positionToAngle(position) {
    return angle360FromPositions(this.props.origin.x, this.props.origin.y, position.x, position.y);
  }

  end(evts) {
    if (this.props.onMoved) this.props.onMoved();
    events.removeEventsFromDocument(evts);
  }

  move(position) {
    const degrees = this.trimAngleToValue(this.positionToAngle(position));
    const radius = this.getPositionRadius(position);
    if (this.props.onMove) this.props.onMove(degrees === 360 ? 0 : degrees, radius);
  }

  render() {
    const { theme } = this.props;
    const className = `${theme.hand} ${this.props.className}`;
    const handStyle = prefixer({
      height: this.props.length - (this.state.knobWidth / 2),
      transform: `rotate(${this.props.angle}deg)`,
    });

    return (
      <div className={className} style={handStyle}>
        <div ref={(node) => { this.knobNode = node; }} className={theme.knob} />
      </div>
    );
  }
}

export default Hand;

import React, { Component, PropTypes } from 'react';
import { range } from '../utils/utils';
import Hand from './ClockHand';
import Face from './ClockFace';

const outerNumbers = [0, ...range(13, 24)];
const innerNumbers = [12, ...range(1, 12)];
const innerSpacing = 1.7;
const step = 360 / 12;

class Hours extends Component {
  static propTypes = {
    center: PropTypes.shape({
      x: PropTypes.number,
      y: PropTypes.number,
    }),
    format: PropTypes.oneOf(['24hr', 'ampm']),
    onChange: PropTypes.func,
    onHandMoved: PropTypes.func,
    radius: PropTypes.number,
    selected: PropTypes.number,
    spacing: PropTypes.number,
    theme: PropTypes.object, // eslint-disable-line
  };

  state = {
    inner: this.props.format === '24hr' && this.props.selected > 0 && this.props.selected <= 12,
  };

  handleHandMove = (degrees, radius) => {
    const currentInner = radius < this.props.radius - (this.props.spacing * innerSpacing);
    if (this.props.format === '24hr' && this.state.inner !== currentInner) {
      this.setState({ inner: currentInner }, () => {
        this.props.onChange(this.valueFromDegrees(degrees));
      });
    } else {
      this.props.onChange(this.valueFromDegrees(degrees));
    }
  };

  handleMouseDown = (event) => {
    this.handNode.mouseStart(event);
  };

  handleTouchStart = (event) => {
    this.handNode.touchStart(event);
  };

  valueFromDegrees(degrees) {
    if (this.props.format === 'ampm' || (this.props.format === '24hr' && this.state.inner)) {
      return innerNumbers[degrees / step];
    }
    return outerNumbers[degrees / step];
  }

  renderInnerFace(innerRadius) {
    if (this.props.format !== '24hr') return undefined;
    return (
      <Face
        onTouchStart={this.handleTouchStart}
        onMouseDown={this.handleMouseDown}
        numbers={innerNumbers}
        spacing={this.props.spacing}
        radius={innerRadius}
        theme={this.props.theme}
        active={this.props.selected}
      />
    );
  }

  render() {
    const { format, selected, radius, spacing, center, onHandMoved } = this.props;
    const is24hr = format === '24hr';

    return (
      <div>
        <Face
          onTouchStart={this.handleTouchStart}
          onMouseDown={this.handleMouseDown}
          numbers={is24hr ? outerNumbers : innerNumbers}
          spacing={spacing}
          radius={radius}
          twoDigits={is24hr}
          active={is24hr ? selected : (selected % 12 || 12)}
          theme={this.props.theme}
        />
        {this.renderInnerFace(radius - (spacing * innerSpacing))}
        <Hand
          ref={(node) => { this.handNode = node; }}
          angle={selected * step}
          length={(this.state.inner ? radius - (spacing * innerSpacing) : radius) - spacing}
          onMove={this.handleHandMove}
          theme={this.props.theme}
          onMoved={onHandMoved}
          origin={center}
          step={step}
        />
      </div>
    );
  }
}

export default Hours;

import React, { Component, PropTypes } from 'react';
import CssTransitionGroup from 'react-addons-css-transition-group';
import { getAnimationModule } from '../utils/utils';
import time from '../utils/time';
import Hours from './ClockHours';
import Minutes from './ClockMinutes';

class Clock extends Component {
  static propTypes = {
    display: PropTypes.oneOf(['hours', 'minutes']),
    format: PropTypes.oneOf(['24hr', 'ampm']),
    onChange: PropTypes.func,
    onHandMoved: PropTypes.func,
    theme: PropTypes.shape({
      clock: PropTypes.string,
      clockWrapper: PropTypes.string,
      placeholder: PropTypes.string,
    }),
    time: PropTypes.instanceOf(Date),
  };

  static defaultProps = {
    className: '',
    display: 'hours',
    format: '24hr',
    time: new Date(),
  };

  state = {
    center: { x: null, y: null },
    radius: 0,
  };

  componentDidMount() {
    window.addEventListener('resize', this.handleCalculateShape);
    setTimeout(() => {
      this.handleCalculateShape();
    });
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleCalculateShape);
  }

  handleHourChange = (hours) => {
    if (this.props.time.getHours() !== hours) {
      this.props.onChange(time.setHours(this.props.time, this.adaptHourToFormat(hours)));
    }
  };

  handleMinuteChange = (minutes) => {
    if (this.props.time.getMinutes() !== minutes) {
      this.props.onChange(time.setMinutes(this.props.time, minutes));
    }
  };

  handleCalculateShape = () => {
    const { top, left, width } = this.placeholderNode.getBoundingClientRect();
    this.setState({
      center: {
        x: left + ((width / 2) - window.pageXOffset),
        y: top + ((width / 2) - window.pageXOffset),
      },
      radius: width / 2,
    });
  };

  adaptHourToFormat(hour) {
    if (this.props.format === 'ampm') {
      if (time.getTimeMode(this.props.time) === 'pm') {
        return hour < 12 ? hour + 12 : hour;
      }
      return hour === 12 ? 0 : hour;
    }
    return hour;
  }

  renderHours() {
    return (
      <Hours
        center={this.state.center}
        format={this.props.format}
        onChange={this.handleHourChange}
        radius={this.state.radius}
        selected={this.props.time.getHours()}
        spacing={this.state.radius * 0.18}
        onHandMoved={this.props.onHandMoved}
        theme={this.props.theme}
      />
    );
  }

  renderMinutes() {
    return (
      <Minutes
        center={this.state.center}
        onChange={this.handleMinuteChange}
        radius={this.state.radius}
        selected={this.props.time.getMinutes()}
        spacing={this.state.radius * 0.18}
        onHandMoved={this.props.onHandMoved}
        theme={this.props.theme}
      />
    );
  }

  render() {
    const { theme } = this.props;
    const animation = this.state.display === 'hours' ? 'zoomOut' : 'zoomIn';
    const animationModule = getAnimationModule(animation, theme);
    return (
      <div data-react-toolbox="clock" className={theme.clock}>
        <div
          className={theme.placeholder}
          style={{ height: this.state.radius * 2 }}
          ref={(node) => { this.placeholderNode = node; }}
        >
          <CssTransitionGroup
            transitionName={animationModule}
            transitionEnterTimeout={500}
            transitionLeaveTimeout={500}
          >
            <div
              key={this.props.display}
              className={theme.clockWrapper}
              style={{ height: this.state.radius * 2 }}
            >
              {this.props.display === 'hours' ? this.renderHours() : null}
              {this.props.display === 'minutes' ? this.renderMinutes() : null}
            </div>
          </CssTransitionGroup>
        </div>
      </div>
    );
  }
}

export default Clock;

